import React, { Component } from 'react';
import axios from 'axios';
import ReactDOM from 'react-dom';
import { Link } from 'react-router-dom';
import './Filter.css';
import RestaurantList from './RestaurantList';
import Costfilter from './filter/Costfilter';
import CusineFilter from './filter/CuisineFilter';


const restdetail = "http://localhost:8900/restauranthome";
class Filter extends Component{
    constructor(){
        super()
        this.state = {
            citiId: parseInt(sessionStorage.getItem('citiId'))? parseInt(sessionStorage.getItem('citiId')):1,
            restlist:[],
        }
    }
    setDataPerCuisine(sortedData){
        this.setState({restlist:sortedData})
    }

    setDataPerCost(sortedData){
        this.setState({restlist:sortedData})
    }


    render() {
        return (
            <body>
                <div class="titlebar">
                    <Link to="/">
                        <div id="logo">
                        </div>
                        
                    </Link>
                    <h2>EDUMATO</h2>
                    
                    <div class = "titlebar__right">
                        <button class="Creataccount">
                            <p>Creataccount</p>
                        </button>
                        <button class="Login">
                            <p>Login</p>
                        </button>
                    </div>
                </div>
                <div className="container">
                    <h1 className="heading">Mealtype places in City</h1>
                    <div class="row">
                        <div className="mealtype col-lg-2" >
                            {/* <center class="titles"><h3>Cuisines</h3><hr /></center>
                            <input type="radio" name="meal" id="1" value="All" /><span class="text">All</span><br />
                            <input type="radio" name="meal" id="2" value="Fastfood" /><span class="text">Fastfood</span><br />
                            <input type="radio" name="meal" id="3" value="South" /><span class="text">South Indian</span><br />
                            <input type="radio" name="meal" id="4" value="Chines" /><span class="text">Chines</span><br />
                            <input type="radio" name="meal" id="5" value="Italian" /><span class="text">Italian</span><br /> */}
                            <CusineFilter mealIdNumber={this.props.match.params.id} citiIdNo={this.state.citiId} restpercuisine={(data) => {this.setDataPerCuisine(data)}}/>
                        

                            <center className="titles"><h3>Cost for two</h3><hr /></center>
                            {/* <input type="radio" name="price" class="" value="" /><span class="text">All</span><br />
                            <input type="radio" name="price" class="" value="" /><span class="text">₹0-₹500</span><br />
                            <input type="radio" name="price" class="" value="" /><span class="text">₹500-₹1000</span><br />
                            <input type="radio" name="price" class="" value="" /><span class="text">₹1000-₹2000</span><br />
                            <input type="radio" name="price" class="" value="" /><span class="text">₹2000+</span><br /> */}
                            <Costfilter restpercost={(data) => {this.setDataPerCost(data)}} mealIdNumber={this.props.match.params.id} citiIdNo={this.state.citiId}/>
                
                            <center className="titles"><h3>Sort</h3><hr /></center>
                            <input type="radio" name="sort" class="" value="" /><span class="text">Price High to Low </span><br />
                            <input type="radio" name="sort" class="" value="" /><span class="text">Price Low to High</span><br />
                        </div>
                    {/* restaurant list */}
                        <div className="rest col-lg-8">
                            <RestaurantList restdata={this.state.restlist}/>
                        </div>
                        <div className="rest col-lg-8">
                            <div className="image">
                            </div>
                            <div className="address">
                                <h2>The Big Chill Chakery</h2><br />
                                <h3>Port</h3><br />
                                <p>Shop1,Plot D,Samruddhi Complex,Chincholi</p>
                            </div>
                            <hr />
                            <div className="price">
                                <p>CUISINES : BAKERY</p>
                                <p>COST FOR TWO:₹700</p>
                            </div>
                        </div>
                    </div>
                </div>
            </body>
        )
    }
    componentDidMount(){
        sessionStorage.setItem('mealid',this.props.match.params.id)
        let mealid = parseInt(this.props.match.params.id)
        axios.get(`${restdetail}/${this.state.citiId}/${mealid}`)
        .then((response) => {this.setState({restlist:response.data})})
    }
}


export default Filter;
