import React,{Component} from 'react';
import { Link } from 'react-router-dom';

const locurl = "http://localhost:8900/location";
const resturl = "http://localhost:8900/restauranthome?city=";

class Banner extends Component {
    constructor(){
        super()
        this.state = {
            locaton:'',
            city:'',
            restaurants:'',
        }   
    }
    handleCity = (event) => {
        this.setState({restaurents:''})
        this.setState({city:event.target.value})
        var cityid = parseInt(event.target.value)
        sessionStorage.setItem('citiId',event.target.value)
        fetch(`${resturl}${cityid}`,{method:'GET'})
        .then((res) => res.json())
        .then((data) => {
            this.setState({restaurents:data})
        })
    }
    renderCity = (data) => {
        if(data){
            return data.map((item) => {
                return(
                    <option value={item.city}>
                        {item.name}
                    </option>
                )
            })
        }
    }

    renderRest = (data) => {
        if(data){
            return data.map((item) => {
                return(
                    <option>
                        {item.name} | {item.locality}
                    </option>
                    
                )
            })
        }
    }
    changeName = (event) => {
        this.setState({name: event.target.value}); 
    }

    sendToParent = () => {
        this.props.searching(this.state.name);
    }
    // Event = () => {
    //     if(true){
    //         this.renderRest()
    //     }else{
    //     alert('please select a location first')
    //     }
    // }

    render(){
        return(
            <div> 
               <div class="imgcontainer">
                    <div class="logo">   
                    </div>
                    <div class="HeadComp">
                        <h2>Find the best Restaurants,Caf√©s & Bars</h2>
                    </div> 
                        <div class="locationselector">
                            <select class="dropdown" onChange = {this.handleCity}>
                            <option value="">Select Location</option>
                                {this.renderCity(this.state.location)}
                            </select>
                            <input class="restaurantsinput" type="text" list="city" placeholder="Search for Restaurants..." onSubmit={this.changeName}/><i class=" fa fa-search"></i>
                            <datalist id="city">
                                {this.renderRest(this.state.restaurents)}
                            </datalist>
                        </div>
                </div>
            </div>
        )
    }
    componentDidMount(){
        fetch(locurl,{method:'GET'})
        .then((res) => res.json())
        .then((data) => {
           
        this.setState({location:data})
        })
     }
     
}

export default Banner;
