import React,{Component} from 'react';

const locurl = "http://localhost:8900/location";
const resturl = "http://localhost:8900/allrestaurants";

class Banner extends Component {
    constructor(){
        super()
        this.state = {
            locaton:'',
            city:'',
            restaurants:'',
        }   
    }
    handleCity = (event) => {
        this.setState({restaurents:''})
        this.setState({city:event.target.value})
        var cityid = parseInt(event.target.value)
        
        fetch(`${resturl}${cityid}`,{method:'GET'})
        .then((res) => res.json())
        .then((data) => {
            this.setState({restaurents:data})
        })
    }
    renderCity = (data) => {
        if(data){
            return data.map((item) => {
                return(
                    <option value={item.city}>
                        {item.name}
                    </option>
                )
            })
        }
    }

    renderRest = (data) => {
        if(data){
            return data.map((item) => {
                return(
                    <option>
                        {item.name} | {item.locality}
                    </option>
                )
            })
        }
    }

    render(){
        return(
            <div> 
               <div class="imgcontainer">
                    <div class="logo">   
                    </div>
                    <div class="HeadComp">
                        <h2>Find the best Restaurants,Caf√©s & Bars</h2>
                    </div> 
                        <div class="locationselector">
                            <select class="dropdown" onChange = {this.handleCity}>
                            <option value="1">Select Location</option>
                                {this.renderCity(this.state.location)}
                                {/* <option value="1">Select Location</option>
                                <option value="2">Andhrapradesh</option>
                                <option value="4">Assam</option>
                                <option value="8">Gujarat</option>
                                <option value="9">Haryana</option>
                                <option value="11">Jharkhand</option>
                                <option value="12">Bhubaneswar</option>
                                <option value="13">Maharashtra</option>
                                <option value="14">Dehli</option>
                                <option value="15">Chandigarh</option>
                                <option value="16">Westbengal</option> */}
                            </select>
                            <input class="restaurantsinput" type="text" list="city" placeholder="Search for Restaurants..."/><i class=" fa fa-search"></i>
                            <datalist id="city">
                            {this.renderRest(this.state.restaurents)}
                                {/* <option>Dehli</option>
                                <option>Mumbai</option>
                                <option>Chandigar</option>
                                <option>Ahmedabad</option>
                                <option>Bhubaneswar</option> */}
                            </datalist>
                        </div>
                </div>
            </div>
        )
    }
    componentDidMount(){
        fetch(locurl,{method:'GET'})
        .then((res) => res.json())
        .then((data) => {
           
            this.setState({location:data})
        })
     }
     
}




export default Banner;
